import React, { useContext } from 'react'
import { Button, Col, ListGroup, Row } from 'react-bootstrap';
import { Helmet } from 'react-helmet-async';
import { Link } from 'react-router-dom';
import MessageBox from '../components/MessageBox';
import { Store } from '../Store'

export default function CartScreen() {
    const {state, dispatch: ctxDispatch} = useContext(Store);

    const {
        cart: {cartItems},
    } = state;
  return (
    <div>
        <Helmet>
            <title>Shopping Cart</title>
        </Helmet>
        <h1>Shopping Cart</h1>
        <Row>
            <Col md={8}>
                {cartItems.length === 0 ? (
                    <MessageBox>
                        Cart is empty. <Link to="/">Let's Shopping</Link>
                    </MessageBox>
                ) : (
                    <ListGroup>
                        {cartItems.map((item)=>(
                            <ListGroup.Item key={item._id}>
                                <Row className='align-items-center'>
                                    <Col md={4}>
                                      <img src={item.image} alt={item.name} className="img-fluid rounded img-thumbnail"></img>{' '}
                                      <Link to={`/api/products/slug/${slug}`}>{item.name}</Link>
                                    </Col>
                                    <Col md={3}>
                                        <Button variant='light' disabled={item.quantity === 1}>
                                            <i className='fas fa-minus-circle'></i>
                                        </Button>{' '}
                                        <span>{item.quantity}</span>{' '}
                                        <Button variant='light' disabled={item.quantity === 1}>
                                            <i className='fas fa-plus-circle'></i>
                                        </Button>
                                    </Col>
                                    <Col md={3}>${item.price}</Col>
                                    <Col>
                                    <Button variant='light'><i className='fas fa-trash'></i></Button>
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                )}
            </Col>
            <Col md={4}></Col>
        </Row>
    </div>
  )
}
